export interface LogContext {
  correlationId: string;
  timestamp: Date;
  service: string;
  [key: string]: unknown;
}

export interface LogEntry {
  level: 'debug' | 'info' | 'warn' | 'error';
  message: string;
  context: LogContext;
  data?: Record<string, unknown>;
}

export class CorrelationIdManager {
  private static storage = new Map<string, string>();

  /**
   * Generate a new correlation ID
   */
  static generate(): string {
    return randomUUID();
  }

  /**
   * Set correlation ID for a given request/context key
   */
  static set(key: string, correlationId: string): void {
    this.storage.set(key, correlationId);
  }

  /**
   * Get correlation ID for a given request/context key
   */
  static get(key: string): string | undefined {
    return this.storage.get(key);
  }

  /**
   * Remove correlation ID when request is complete
   */
  static clear(key: string): void {
    this.storage.delete(key);
  }
}

export class Logger {
  private serviceName: string;
  private correlationId: string | undefined;

  constructor(serviceName: string, correlationId?: string) {
    this.serviceName = serviceName;
    this.correlationId = correlationId;
  }

  /**
   * Create a child logger with a specific correlation ID
   */
  withCorrelationId(correlationId: string): Logger {
    return new Logger(this.serviceName, correlationId);
  }

  /**
   * Get current correlation ID or generate a new one
   */
  getCorrelationId(): string {
    if (!this.correlationId) {
      this.correlationId = CorrelationIdManager.generate();
    }
    return this.correlationId;
  }

  private createLogEntry(
    level: LogEntry['level'],
    message: string,
    data?: Record<string, unknown>
  ): LogEntry {
    return {
      level,
      message,
      context: {
        correlationId: this.getCorrelationId(),
        timestamp: new Date(),
        service: this.serviceName,
      },
      data,
    };
  }

  private output(entry: LogEntry): void {
    const formatted = JSON.stringify(entry, null, 2);
    console.log(formatted);
  }

  debug(message: string, data?: Record<string, unknown>): void {
    this.output(this.createLogEntry('debug', message, data));
  }

  info(message: string, data?: Record<string, unknown>): void {
    this.output(this.createLogEntry('info', message, data));
  }

  warn(message: string, data?: Record<string, unknown>): void {
    this.output(this.createLogEntry('warn', message, data));
  }

  error(message: string, data?: Record<string, unknown>): void {
    this.output(this.createLogEntry('error', message, data));
  }
}

export default Logger;
