describe('Correlation ID Tests', () => {
  describe('CorrelationIdManager', () => {
    afterEach(() => {
      // Clean up any stored correlation IDs
      CorrelationIdManager.clear('test-key');
    });

    test('should generate unique correlation IDs', () => {
      const id1 = CorrelationIdManager.generate();
      const id2 = CorrelationIdManager.generate();

      expect(id1).toBeDefined();
      expect(id2).toBeDefined();
      expect(id1).not.toBe(id2);
    });

    test('should generate valid UUID format', () => {
      const id = CorrelationIdManager.generate();
      const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;

      expect(id).toMatch(uuidRegex);
    });

    test('should store and retrieve correlation IDs', () => {
      const correlationId = 'test-correlation-123';
      CorrelationIdManager.set('test-key', correlationId);

      expect(CorrelationIdManager.get('test-key')).toBe(correlationId);
    });

    test('should return undefined for non-existent keys', () => {
      expect(CorrelationIdManager.get('non-existent-key')).toBeUndefined();
    });

    test('should clear correlation IDs', () => {
      const correlationId = 'test-correlation-456';
      CorrelationIdManager.set('test-key', correlationId);
      CorrelationIdManager.clear('test-key');

      expect(CorrelationIdManager.get('test-key')).toBeUndefined();
    });
  });

  describe('Logger with Correlation IDs', () => {
    let consoleSpy: jest.SpyInstance;

    beforeEach(() => {
      consoleSpy = jest.spyOn(console, 'log').mockImplementation();
    });

    afterEach(() => {
      consoleSpy.mockRestore();
    });

    test('should auto-generate correlation ID when not provided', () => {
      const logger = new Logger('test-service');
      const correlationId = logger.getCorrelationId();

      expect(correlationId).toBeDefined();
      expect(correlationId.length).toBeGreaterThan(0);
    });

    test('should use provided correlation ID', () => {
      const customCorrelationId = 'custom-correlation-789';
      const logger = new Logger('test-service', customCorrelationId);

      expect(logger.getCorrelationId()).toBe(customCorrelationId);
    });

    test('should maintain same correlation ID across multiple log calls', () => {
      const logger = new Logger('test-service');
      const firstId = logger.getCorrelationId();
      const secondId = logger.getCorrelationId();

      expect(firstId).toBe(secondId);
    });

    test('should include correlation ID in log output', () => {
      const correlationId = 'log-test-correlation';
      const logger = new Logger('test-service', correlationId);

      logger.info('Test message');

      expect(consoleSpy).toHaveBeenCalled();
      const logOutput = consoleSpy.mock.calls[0][0];
      const parsed = JSON.parse(logOutput);

      expect(parsed.context.correlationId).toBe(correlationId);
    });

    test('should create child logger with specific correlation ID', () => {
      const parentLogger = new Logger('test-service');
      const childCorrelationId = 'child-correlation-id';
      const childLogger = parentLogger.withCorrelationId(childCorrelationId);

      expect(childLogger.getCorrelationId()).toBe(childCorrelationId);
    });

    test('should preserve correlation ID across service boundaries', () => {
      const correlationId = 'cross-service-correlation';

      // Simulate Service A
      const serviceALogger = new Logger('service-a', correlationId);
      serviceALogger.info('Processing request');

      // Simulate passing correlation ID to Service B
      const serviceBLogger = new Logger('service-b', correlationId);
      serviceBLogger.info('Handling forwarded request');

      expect(consoleSpy).toHaveBeenCalledTimes(2);

      const serviceALog = JSON.parse(consoleSpy.mock.calls[0][0]);
      const serviceBLog = JSON.parse(consoleSpy.mock.calls[1][0]);

      expect(serviceALog.context.correlationId).toBe(serviceBLog.context.correlationId);
      expect(serviceALog.context.service).toBe('service-a');
      expect(serviceBLog.context.service).toBe('service-b');
    });

    test('should include all required context fields in log entry', () => {
      const correlationId = 'context-test-correlation';
      const logger = new Logger('test-service', correlationId);

      logger.info('Test message', { userId: 123 });

      const logOutput = JSON.parse(consoleSpy.mock.calls[0][0]);

      expect(logOutput).toMatchObject({
        level: 'info',
        message: 'Test message',
        context: {
          correlationId: correlationId,
          service: 'test-service',
        },
        data: { userId: 123 },
      });
      expect(logOutput.context.timestamp).toBeDefined();
    });
  });

  describe('Correlation ID Propagation Scenarios', () => {
    let consoleSpy: jest.SpyInstance;

    beforeEach(() => {
      consoleSpy = jest.spyOn(console, 'log').mockImplementation();
    });

    afterEach(() => {
      consoleSpy.mockRestore();
    });

    test('should trace request through multiple operations', () => {
      const correlationId = 'request-trace-123';
      const logger = new Logger('api-service', correlationId);

      // Simulate a request flow
      logger.info('Received HTTP request', { method: 'POST', path: '/users' });
      logger.debug('Validating request body');
      logger.info('Request validated successfully');
      logger.info('Calling database');
      logger.info('Database operation complete');
      logger.info('Sending response', { status: 201 });

      expect(consoleSpy).toHaveBeenCalledTimes(6);

      // Verify all logs have the same correlation ID
      for (let i = 0; i < 6; i++) {
        const log = JSON.parse(consoleSpy.mock.calls[i][0]);
        expect(log.context.correlationId).toBe(correlationId);
      }
    });

    test('should handle concurrent requests with different correlation IDs', () => {
      const request1Logger = new Logger('api-service', 'request-1-correlation');
      const request2Logger = new Logger('api-service', 'request-2-correlation');

      // Interleaved log calls (simulating concurrent requests)
      request1Logger.info('Processing request 1');
      request2Logger.info('Processing request 2');
      request1Logger.info('Request 1 complete');
      request2Logger.info('Request 2 complete');

      expect(consoleSpy).toHaveBeenCalledTimes(4);

      const log1a = JSON.parse(consoleSpy.mock.calls[0][0]);
      const log2a = JSON.parse(consoleSpy.mock.calls[1][0]);
      const log1b = JSON.parse(consoleSpy.mock.calls[2][0]);
      const log2b = JSON.parse(consoleSpy.mock.calls[3][0]);

      expect(log1a.context.correlationId).toBe('request-1-correlation');
      expect(log2a.context.correlationId).toBe('request-2-correlation');
      expect(log1b.context.correlationId).toBe('request-1-correlation');
      expect(log2b.context.correlationId).toBe('request-2-correlation');
    });
  });
});
